$(function() {$.fn.addTag=function(tag,attrs,content,css){tag=$.trim(tag);attrs=$.trim(attrs);content=$.trim(content);css=$.trim(css);if(tag!=null&&tag!=""&&tag!=" "){switch(tag){case"br":$(this).append("<br />");break;case"hr":$(this).append("<hr />");break;case"img":$(this).append("<img />");break;case"input":$(this).append("<input />");break;case"meta":$(this).append("<meta />");break;default:$(this).append("<"+tag+"></"+tag+">");break;}if(content!=null&&content!=""&&content!=" "){$(this).children(tag).last().html(content);}if(attrs!=null &&attrs!=""&&attrs!=" "&&attrs.indexOf("{")==0&&attrs.lastIndexOf("}")){var atr=$.trim(attrs.substr(1,attrs.length-2));if(atr!=null&&atr!=""&&atr!=" "){if(atr.indexOf(",")>=2){var attributes=atr.split(",");for(var i=0;i<=attributes.length-1;i++){var names=attributes[i].split(":");$(this).children(tag).last().attr($.trim(names[0]),$.trim(names[1]));}}else{var attributes=atr.split(":");$(this).children(tag).last().attr($.trim(attributes[0]),$.trim(attributes[1]));}}}if(css!=null&&css!=""&&css!=" "&&css.indexOf("{")==0&&css.lastIndexOf("}")){var atr=$.trim(css.substr(1,css.length-2));if(atr!=null&&atr!=""&&atr!=" "){if(atr.indexOf(",")>=2){var attributes = atr.split(",");for(var i=0;i<=attributes.length-1;i++){var names=attributes[i].split(":");$(this).children(tag).last().css($.trim(names[0]),$.trim(names[1]));}}else{var attributes=atr.split(":");$(this).children(tag).last().css($.trim(attributes[0]),$.trim(attributes[1]));}}}}else{console.error("Please select the tag name");}}});